Password Strength Checker
A practical tool for validating password complexity:
import re
import string

def check_password_strength(password):
    """
    Comprehensive password strength checker
    """
    score = 0
    feedback = []

    # Length check
    if len(password) &gt;= 12:
        score += 2
    elif len(password) &gt;= 8:
        score += 1
    else:
        feedback.append("Password should be at least 8 characters long")

    # Character diversity checks
    if re.search(r'[a-z]', password):
        score += 1
    else:
        feedback.append("Add lowercase letters")

    if re.search(r'[A-Z]', password):
        score += 1
    else:
        feedback.append("Add uppercase letters")

    if re.search(r'\d', password):
        score += 1
    else:
        feedback.append("Add numbers")

    if re.search(r'[!@#$%^&*(),.?":{}|&lt;&gt;]', password):
        score += 1
    else:
        feedback.append("Add special characters")

    # Advanced checks
    if not re.search(r'(.)\1{2,}', password):  # No repeated characters
        score += 1
    else:
        feedback.append("Avoid repeated characters")

    # Strength rating
    if score &gt;= 6:
        strength = "Strong"
    elif score &gt;= 4:
        strength = "Medium"
    else:
        strength = "Weak"

    return {
        'score': score,
        'strength': strength,
        'feedback': feedback
    }

# Example usage
def test_passwords():
    test_cases = [
        "password123",
        "P@ssw0rd!",
        "MyVerySecureP@ssw0rd2024!"
    ]

    for pwd in test_cases:
        result = check_password_strength(pwd)
        print(f"Password: {pwd}")
        print(f"Strength: {result['strength']} (Score: {result['score']}/7)")
        if result['feedback']:
            print("Recommendations:")
            for tip in result['feedback']:
                print(f"  - {tip}")
        print("-" * 40)

test_passwords()

